-- ===== Update clubs.memberCount to match actual inserted people (counts based on above inserts) =====
UPDATE clubs SET memberCount = (
  SELECT COUNT(*) FROM person WHERE person.club = clubs.clubName AND person.role IN ('CL','VP','CM')
);
