INSERT INTO clubs (`clubName`, `description`, `memberCount`, `memberMax`, `bannerImage`, `bannerColor`) VALUES
('Ski', '<p>The Ski Club meets every winter. Equipment rentals available.<br><b>Weekly schedule:</b><ul><li>Mon: dryland training 18:00-19:30</li><li>Fri: slope trips (depart 06:00)</li></ul><p style="color:blue">Après-ski socials every Saturday night.</p>', 18, 40, 'https://www.shutterstock.com/image-photo/snowboarder-skiing-downhill-panoramic-format-260nw-1203033724.jpg', NULL),
('Basketball', '<p>Open hoops and competitive teams. Practices: Tue/Thu 20:00-22:00 at Gym A.<br><small>Junior sessions on Sat 10:00-12:00</small></p>', 22, 50, 'https://images.unsplash.com/photo-1546519638-68e109498ffc?w=800', NULL),
('Robotics', '<p>We build robots and compete in regional challenges. Workshop: Wed 19:00-22:00 (Lab 3)</p>', 15, 40, 'https://img.freepik.com/free-photo/human-robot-interaction-digital-world_23-2151966708.jpg?semt=ais_hybrid&w=740&q=80', '#8b5cf6'),
('Book', '<p>Monthly book selections and two discussion groups. Next meeting: last Tuesday of month, 19:30.</p>', 12, 30, NULL, '#f59e0b'),
('Photography', '<p>Photo walks, editing workshops, and exhibitions. Gear share available on demand.</p>', 10, 25, 'https://images.unsplash.com/photo-1452587925148-ce544e77e70d?w=800', NULL),
('Cooking', '<p>Hands-on cooking sessions and themed dinners. Allergy-friendly options available. <br/>Every 2nd Sunday: community dinner.</p>', 14, 30, 'https://as2.ftcdn.net/jpg/01/57/28/31/1000_F_157283191_ALJy3vuwlPKVdDtPPCZsPpRk3BksFlwF.jpg', '#ef4444'),
('Music', '<p>Chamber groups and open jam nights. Rehearsals Wed 18:00-20:30.</p>', 16, 40, NULL, '#ec4899'),
('Chess', '<p>Casual play and weekly rated matches. Coaching for beginners.</p>', 9, 20, 'https://studycampus.in/wp-content/uploads/2018/12/banner-chess.jpg', '#6366f1'),
('Hiking', '<p>Day hikes and overnight treks. Safety briefing mandatory for multi-day trips.</p>', 13, 35, 'https://images.unsplash.com/photo-1551632811-561732d1e306?w=800', NULL),
('Gaming', '<p>Board games, tabletop RPGs and esports viewing parties. Newcomers welcome.</p>', 20, 60, 'https://cdn.wallpapersafari.com/71/44/yai5Xs.jpeg', '#8b5cf6'),
('Debate', '<p>British parliamentary and public speaking practice. Weekly scrimmages on Thu 18:30.</p>', 11, 30, NULL, '#0ea5e9'),
('Drama', '<p>Scene studies, improv, and semester productions. Rehearsals Mon/Wed 19:00.</p>', 14, 35, NULL, '#f43f5e'),
('Environmental', '<p>Campus sustainability projects, cleanups, and green tech talks.</p>', 10, 40, NULL, '#10b981'),
('Coding', '<p>Weekly coding dojos, hack nights, and interview prep. All levels welcome.</p>', 18, 50, 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSovqFy3SR7gBfblIO0TW7c5B3IjOxB-74tryLt9D9b_uLfvMhAtWvVRJnoLRot3fsWnoY&usqp=CAU', '#3b82f6'),
('Dance', '<p>Contemporary, hip-hop, and salsa sessions. Open classes Tue/Fri evenings.</p>', 15, 40, 'https://images.unsplash.com/photo-1504609813442-a8924e83f76e?w=800', NULL),
('Agartha', '<p>Welcome to the underground. We discuss conspiracy theories, ancient civilizations, and the mysteries of the hollow earth while consuming copious amounts of Monster Ultra White. <b>Brain rot enthusiasts only.</b><br><small>Meetings: Tue/Thu 23:00-02:00 in the basement</small><br><i>"The truth is down there."</i></p>', 7, 15, 'https://i.ytimg.com/vi/3hVXt9NYiJA/maxresdefault.jpg', '#e0e0e0');
